    <!--  -->
    <style>
        /*  +  */
        body {
            margin: 0;
            padding: 0;
            background: linear-gradient(135deg, #1a1a2e, #16213e, #0f3460);
            min-height: 100vh;
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            overflow-x: hidden;
        }
        /*  */
        body::before {
            content: '';
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: radial-gradient(circle at 20% 80%, rgba(120, 119, 198, 0.1) 0%, transparent 50%),
                        radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.1) 0%, transparent 50%),
                        radial-gradient(circle at 40% 40%, rgba(119, 198, 255, 0.1) 0%, transparent 50%);
            z-index: -1;
        }
        /*  -  +  */
        .home-container {
            max-width: 800px;
            margin: 60px auto;
            padding: 50px 30px;
            text-align: center;
            background: rgba(255, 255, 255, 0.05);
            backdrop-filter: blur(15px);
            border-radius: 20px;
            border: 1px solid rgba(255, 255, 255, 0.1);
            box-shadow: 0 8px 32px rgba(0, 0, 0, 0.2);
            position: relative;
        }
        /*  */
        .home-container::after {
            content: '';
            position: absolute;
            top: -2px;
            left: -2px;
            right: -2px;
            bottom: -2px;
            background: linear-gradient(45deg, #ff00cc, #3333ff, #00ccff);
            border-radius: 22px;
            z-index: -1;
            animation: glow 3s linear infinite alternate;
            opacity: 0.7;
        }
        @keyframes glow {
            0% { background-position: 0% 50%; }
            100% { background-position: 100% 50%; }
        }
        /*  -  hover +  */
        .avatar {
            width: 150px;
            height: 150px;
            border-radius: 50%;
            border: 4px solid rgba(255, 255, 255, 0.2);
            margin-bottom: 25px;
            box-shadow: 0 0 25px rgba(120, 119, 198, 0.5);
            transition: all 0.5s ease;
            object-fit: cover;
        }
        .avatar:hover {
            transform: rotate(5deg) scale(1.05);
            box-shadow: 0 0 40px rgba(120, 119, 198, 0.8);
            border-color: rgba(255, 255, 255, 0.4);
        }
        /* / -  +  */
        .nickname {
            font-size: 36px;
            font-weight: 700;
            background: linear-gradient(90deg, #ff7eb3, #7e7eff, #7effd4);
            -webkit-background-clip: text;
            background-clip: text;
            color: transparent;
            margin-bottom: 15px;
            text-shadow: 0 0 10px rgba(120, 119, 198, 0.3);
            letter-spacing: 2px;
        }
        /*  -  */
        .intro {
            font-size: 18px;
            color: #e0e0ff;
            line-height: 1.8;
            margin-bottom: 40px;
            text-shadow: 0 0 8px rgba(120, 119, 198, 0.2);
            max-width: 600px;
            margin-left: auto;
            margin-right: auto;
        }
        /*  */
        .nav-buttons {
            display: flex;
            justify-content: center;
            gap: 20px;
            flex-wrap: wrap;
            margin-bottom: 50px;
        }
        /*  -  + 3D  +  hover */
        .nav-btn {
            padding: 12px 28px;
            background: linear-gradient(45deg, #4285f4, #7e7eff);
            color: white;
            border-radius: 12px;
            text-decoration: none;
            transition: all 0.3s ease;
            font-weight: 600;
            letter-spacing: 1px;
            box-shadow: 0 4px 15px rgba(66, 133, 244, 0.3);
            position: relative;
            overflow: hidden;
            border: none;
        }
        /*  */
        .nav-btn::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
            transition: all 0.5s ease;
        }
        .nav-btn:hover {
            background: linear-gradient(45deg, #3367d6, #6a6aff);
            transform: translateY(-3px);
            box-shadow: 0 8px 20px rgba(66, 133, 244, 0.5);
        }
        .nav-btn:hover::before {
            left: 100%;
        }
        .nav-btn:active {
            transform: translateY(1px);
            box-shadow: 0 4px 10px rgba(66, 133, 244, 0.3);
        }
        /*  */
        .stats {
            display: flex;
            justify-content: center;
            gap: 40px;
            color: #b0b0ff;
            font-size: 16px;
            padding-top: 20px;
            border-top: 1px solid rgba(255, 255, 255, 0.1);
        }
        /*  hover  */
        .stats > div {
            transition: all 0.3s ease;
            padding: 8px 15px;
            border-radius: 8px;
        }
        .stats > div:hover {
            color: #ffffff;
            background: rgba(255, 255, 255, 0.05);
            transform: scale(1.05);
        }
        /*  */
        @media (max-width: 600px) {
            .home-container {
                margin: 30px 20px;
                padding: 40px 20px;
            }
            .nickname {
                font-size: 28px;
            }
            .nav-buttons {
                gap: 15px;
            }
            .nav-btn {
                padding: 10px 20px;
                font-size: 14px;
            }
            .stats {
                flex-direction: column;
                gap: 15px;
            }
        }
    </style>
</head>
<body>
    <div class="home-container">
        <!--  -->
        <img src="./images/avatar.jpg" alt="" class="avatar">
        <!--  -->
        <div class="nickname"></div>
        <!--  -->
        <div class="intro">
            <br>

        </div>
        <!--  -->
        <div class="nav-buttons">
            <a href="/" class="nav-btn"></a>
            <a href="/archives/" class="nav-btn"></a>
            <a href="/tags/" class="nav-btn"></a>
            <a href="/about/" class="nav-btn"></a>
        </div>
        <!--  -->
        <div class="stats">
            <div id="article-count">...</div>
            <div id="current-time">...</div>
        </div>
    </div>
    <script>
 // 1. 
        function updateCurrentTime() {
            const now = new Date();
            // .. ::
            const year = now.getFullYear();
            const month = String(now.getMonth() + 1).padStart(2, '0'); // 00
            const day = String(now.getDate()).padStart(2, '0');
            const hours = String(now.getHours()).padStart(2, '0');
            const minutes = String(now.getMinutes()).padStart(2, '0');
            const seconds = String(now.getSeconds()).padStart(2, '0');
            const timeStr = `${year}.${month}.${day} ${hours}:${minutes}:${seconds}`;
            document.getElementById('current-time').textContent = `${timeStr}`;
        }
        // content.json
        function getArticleCount() {
            fetch('/content.json') // 
                .then(res => {
                    if (res.ok) return res.json();
                    // 
                    throw new Error('');
                })
                .then(data => {
                    // data.posts.length 
                    document.getElementById('article-count').innerText = `${data.posts.length}`;
                })
                .catch(() => {
                    // 
                    document.getElementById('article-count').innerText = '8';
                });
        }
        // 
        updateCurrentTime();
        getArticleCount();
        // 
        setInterval(updateCurrentTime, 1000);
</script>

</body>
</html>

